      setIsTaringState();
      setIsTaringState2();

      model.addObserver('canTare', setCanTareState);
      model.addObserver('canTare2', setCanTareState2);
      setCanTareState();
      setCanTareState2();

      model.addPropertyDescriptionObserver('sensorType', setSensorTypeDisabledState);
